import { useState, useEffect } from "react";
import { Switch, Route } from "wouter";
import { queryClient } from "./lib/queryClient";
import { QueryClientProvider } from "@tanstack/react-query";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import NotFound from "@/pages/not-found";

// Import components
import LandingPage from "@/components/LandingPage";
import OnboardingWizard from "@/components/OnboardingWizard";
import Dashboard from "@/components/Dashboard";
import FeatureSelection from "@/components/FeatureSelection";
import ThemeToggle from "@/components/ThemeToggle";
import type { Project } from "@/components/Dashboard";

// Mock user state for prototype
type UserRole = 'guest' | 'client' | 'admin';

function Router() {
  const [currentView, setCurrentView] = useState<'landing' | 'onboarding' | 'dashboard' | 'features'>('landing');
  const [userRole, setUserRole] = useState<UserRole>('guest');

  const setCurrentViewWrapper = (view: 'landing' | 'onboarding' | 'dashboard' | 'features') => {
    console.log('[DEBUG] setCurrentView called:', currentView, '->', view);
    setCurrentView(view);
  };

  // Mock projects data
  const [projects] = useState<Project[]>([
    {
      id: '1',
      name: 'Acme Design Studio Website',
      status: 'in_progress',
      createdAt: '2024-01-15T10:00:00Z',
      updatedAt: '2024-01-20T14:30:00Z',
      type: 'Service Business',
      progress: 65
    },
    {
      id: '2',
      name: 'Local Bakery Online Store',
      status: 'completed',
      createdAt: '2024-01-10T09:00:00Z',
      updatedAt: '2024-01-25T16:00:00Z',
      type: 'E-commerce',
      progress: 100
    }
  ]);

  const handleGetStarted = () => {
    setCurrentViewWrapper('onboarding');
    setUserRole('client');
    console.log('Starting onboarding process...');
  };

  const handleLogin = () => {
    setCurrentViewWrapper('dashboard');
    setUserRole('client');
    console.log('Logging in as client...');
  };

  const handleCreateProject = () => {
    setCurrentViewWrapper('onboarding');
    console.log('Creating new project...');
  };

  const handleViewProject = (id: string) => {
    console.log('Viewing project:', id);
  };

  const handleEditProject = (id: string) => {
    setCurrentViewWrapper('onboarding');
    console.log('Editing project:', id);
  };

  const handleExportProject = (id: string) => {
    console.log('Exporting project:', id);
  };

  // Render current view
  const renderCurrentView = () => {
    switch (currentView) {
      case 'landing':
        return (
          <LandingPage 
            onGetStarted={handleGetStarted}
            onLogin={handleLogin}
          />
        );

      case 'onboarding':
        return (
          <div className="min-h-screen bg-background">
            <header className="border-b p-4">
              <div className="max-w-6xl mx-auto flex items-center justify-between">
                <div className="flex items-center space-x-4">
                  <button 
                    onClick={() => setCurrentViewWrapper('dashboard')}
                    className="text-sm text-muted-foreground hover:text-foreground"
                    data-testid="button-back-to-dashboard"
                  >
                    â† Back to Dashboard
                  </button>
                  <h1 className="text-lg font-semibold">Client Onboarding Portal</h1>
                </div>
                <ThemeToggle />
              </div>
            </header>
            <div className="p-6">
              <OnboardingWizard key="stable-onboarding-wizard" />
            </div>
          </div>
        );

      case 'dashboard':
        return (
          <div className="min-h-screen bg-background">
            <header className="border-b p-4">
              <div className="max-w-7xl mx-auto flex items-center justify-between">
                <div className="flex items-center space-x-4">
                  <button 
                    onClick={() => setCurrentViewWrapper('landing')}
                    className="text-sm text-muted-foreground hover:text-foreground"
                    data-testid="button-back-to-landing"
                  >
                    â† Back to Landing
                  </button>
                  <h1 className="text-lg font-semibold">WebStudio Pro</h1>
                  <button
                    onClick={() => setCurrentViewWrapper('features')}
                    className="text-xs bg-secondary text-secondary-foreground px-3 py-1.5 rounded hover-elevate"
                    data-testid="button-go-to-features"
                  >
                    Feature Selection
                  </button>
                  {userRole === 'admin' && (
                    <button
                      onClick={() => setUserRole('client')}
                      className="text-xs bg-primary text-primary-foreground px-2 py-1 rounded"
                      data-testid="button-switch-to-client"
                    >
                      Switch to Client View
                    </button>
                  )}
                  {userRole === 'client' && (
                    <button
                      onClick={() => setUserRole('admin')}
                      className="text-xs bg-primary text-primary-foreground px-2 py-1 rounded"
                      data-testid="button-switch-to-admin"
                    >
                      Switch to Admin View
                    </button>
                  )}
                </div>
                <ThemeToggle />
              </div>
            </header>
            <div className="max-w-7xl mx-auto p-6">
              <Dashboard
                projects={projects}
                userRole={userRole as 'client' | 'admin'}
                onCreateProject={handleCreateProject}
                onViewProject={handleViewProject}
                onEditProject={handleEditProject}
                onExportProject={handleExportProject}
              />
            </div>
          </div>
        );

      case 'features':
        return (
          <div className="min-h-screen bg-background">
            <header className="border-b p-4">
              <div className="max-w-7xl mx-auto flex items-center justify-between">
                <div className="flex items-center space-x-4">
                  <button 
                    onClick={() => setCurrentViewWrapper('dashboard')}
                    className="text-sm text-muted-foreground hover:text-foreground"
                    data-testid="button-back-to-dashboard-from-features"
                  >
                    â† Back to Dashboard
                  </button>
                  <h1 className="text-lg font-semibold">WebStudio Pro - Feature Selection</h1>
                </div>
                <ThemeToggle />
              </div>
            </header>
            <div className="max-w-4xl mx-auto p-6">
              <FeatureSelection
                userId="mock-user-id"
                onSaved={() => {
                  console.log('Features saved successfully');
                }}
              />
            </div>
          </div>
        );

      default:
        return <NotFound />;
    }
  };

  return (
    <Switch>
      <Route path="/" component={() => renderCurrentView()} />
      <Route component={NotFound} />
    </Switch>
  );
}

function App() {
  return (
    <QueryClientProvider client={queryClient}>
      <TooltipProvider>
        <Toaster />
        <Router />
      </TooltipProvider>
    </QueryClientProvider>
  );
}

export default App;

client/src/index.css.content:
@tailwind base;
@tailwind components;
@tailwind utilities;

/* LIGHT MODE */
:root {
  --button-outline: rgba(0,0,0, .10);
  --badge-outline: rgba(0,0,0, .05);

  /* Automatic computation of border around primary / danger buttons */
  --opaque-button-border-intensity: -8; /* In terms of percentages */

  /* Backgrounds applied on top of other backgrounds when hovered/active */
  --elevate-1: rgba(0,0,0, .03);
  --elevate-2: rgba(0,0,0, .08);

  --background: 210 20% 98%;

  --foreground: 220 13% 14%;

  --border: 210 20% 89%;

  --card: 210 20% 96%;

  --card-foreground: 220 13% 14%;

  --card-border: 210 20% 91%;

  --sidebar: 210 20% 94%;

  --sidebar-foreground: 220 13% 14%;

  --sidebar-border: 210 20% 87%;

  --sidebar-primary: 255 87% 51%;

  --sidebar-primary-foreground: 255 100% 95%;

  --sidebar-accent: 210 20% 88%;

  --sidebar-accent-foreground: 220 13% 14%;

  --sidebar-ring: 255 87% 51%;

  --popover: 210 20% 92%;

  --popover-foreground: 220 13% 14%;

  --popover-border: 210 20% 85%;

  --primary: 255 87% 51%;

  --primary-foreground: 255 100% 95%;

  --secondary: 210 20% 88%;

  --secondary-foreground: 220 13% 14%;

  --muted: 210 20% 90%;

  --muted-foreground: 220 13% 45%;

  --accent: 210 15% 86%;

  --accent-foreground: 220 13% 14%;

  --destructive: 0 84% 60%;

  --destructive-foreground: 0 100% 98%;

  --input: 210 20% 82%;
  --ring: 255 87% 51%;
  --chart-1: 255 77% 41%;
  --chart-2: 142 76% 36%;
  --chart-3: 38 92% 50%;
  --chart-4: 280 80% 50%;
  --chart-5: 20 85% 55%;

  --font-sans: Inter, sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: Menlo, monospace;
  --radius: .5rem; /* 8px */
  --shadow-2xs: 0px 2px 0px 0px hsl(210 20% 84% / 0.20);
  --shadow-xs: 0px 2px 0px 0px hsl(210 20% 82% / 0.25);
  --shadow-sm: 0px 2px 0px 0px hsl(210 20% 82% / 0.15), 0px 1px 2px -1px hsl(210 20% 80% / 0.30);
  --shadow: 0px 2px 0px 0px hsl(210 20% 80% / 0.20), 0px 1px 2px -1px hsl(210 20% 78% / 0.35);
  --shadow-md: 0px 2px 0px 0px hsl(210 20% 78% / 0.25), 0px 2px 4px -1px hsl(210 20% 76% / 0.40);
  --shadow-lg: 0px 2px 0px 0px hsl(210 20% 76% / 0.30), 0px 4px 6px -1px hsl(210 20% 74% / 0.45);
  --shadow-xl: 0px 2px 0px 0px hsl(210 20% 74% / 0.35), 0px 8px 10px -1px hsl(210 20% 72% / 0.50);
  --shadow-2xl: 0px 2px 0px 0px hsl(210 20% 72% / 0.40);
  --tracking-normal: 0em;
  --spacing: 0.25rem;

  /* Automatically computed borders - intensity can be controlled by the user by the --opaque-button-border-intensity setting */

  /* Fallback for older browsers */
  --sidebar-primary-border: hsl(var(--sidebar-primary));
  --sidebar-primary-border: hsl(from hsl(var(--sidebar-primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --sidebar-accent-border: hsl(var(--sidebar-accent));
  --sidebar-accent-border: hsl(from hsl(var(--sidebar-accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --primary-border: hsl(var(--primary));
  --primary-border: hsl(from hsl(var(--primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --secondary-border: hsl(var(--secondary));
  --secondary-border: hsl(from hsl(var(--secondary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --muted-border: hsl(var(--muted));
  --muted-border: hsl(from hsl(var(--muted)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --accent-border: hsl(var(--accent));
  --accent-border: hsl(from hsl(var(--accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --destructive-border: hsl(var(--destructive));
  --destructive-border: hsl(from hsl(var(--destructive)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
}

.dark {
  --button-outline: rgba(255,255,255, .10);
  --badge-outline: rgba(255,255,255, .05);

  --opaque-button-border-intensity: 9;  /* In terms of percentages */

  /* Backgrounds applied on top of other backgrounds when hovered/active */
  --elevate-1: rgba(255,255,255, .04);
  --elevate-2: rgba(255,255,255, .09);

  --background: 220 13% 14%;

  --foreground: 210 20% 92%;

  --border: 220 13% 20%;

  --card: 220 13% 16%;

  --card-foreground: 210 20% 92%;

  --card-border: 220 13% 22%;

