  --sidebar-primary: 255 77% 41%;

  --sidebar-primary-foreground: 255 100% 95%;

  --sidebar-accent: 220 13% 22%;

  --sidebar-accent-foreground: 210 20% 92%;

  --sidebar-ring: 255 77% 41%;

  --popover: 220 13% 20%;

