                </div>
              )}

              {briefData.designNotes && (
                <div>
                  <p className="text-sm font-medium mb-2">Notes:</p>
                  <p className="text-sm text-muted-foreground leading-relaxed">
                    {briefData.designNotes}
                  </p>
                </div>
              )}
