          </CardContent>
        </Card>
      </div>

      {/* Content & Media */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <FileText className="w-5 h-5 text-primary" />
            Content & Media
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {/* Page Content */}
            <div>
              <div className="flex items-center justify-between mb-4">
                <h4 className="font-semibold">Page Content</h4>
                <Button 
                  variant="ghost" 
                  size="sm" 
                  onClick={() => onEditSection('content')}
                  data-testid="button-edit-content"
                >
                  Edit
                </Button>
              </div>
              <div className="space-y-3">
                {Object.entries(briefData.pageContent).slice(0, 4).map(([pageName, content]) => (
                  <div key={pageName} className="border-l-2 border-primary pl-3">
                    <p className="text-sm font-medium">{pageName}</p>
                    <p className="text-sm text-muted-foreground line-clamp-2">
                      {content || 'Content pending...'}
                    </p>
                  </div>
                ))}
                {Object.keys(briefData.pageContent).length > 4 && (
                  <p className="text-sm text-muted-foreground">
                    +{Object.keys(briefData.pageContent).length - 4} more pages
                  </p>
                )}
              </div>
            </div>

            {/* Integrations */}
            {briefData.crmIntegration && (briefData.crmIntegration.selectedCrms?.length > 0 || briefData.crmIntegration.selectedMarketingAutomation?.length > 0 || briefData.crmIntegration.selectedPaymentGateways?.length > 0 || briefData.crmIntegration.apiIntegrations?.trim() || briefData.crmIntegration.selectedAutomationPlatforms?.length > 0 || briefData.crmIntegration.selectedEngagementFeatures?.length > 0 || (briefData.crmIntegration.selectedAdvancedFeatures && briefData.crmIntegration.selectedAdvancedFeatures.length > 0) || (briefData.crmIntegration.selectedECommerceFeatures && briefData.crmIntegration.selectedECommerceFeatures.length > 0)) && (
              <div>
                <div className="flex items-center justify-between mb-4">
                  <h4 className="font-semibold">Integrations</h4>
                  <Button 
                    variant="ghost" 
                    size="sm" 
                    onClick={() => onEditSection('integration')}
                    data-testid="button-edit-integration"
                  >
                    Edit
                  </Button>
                </div>
                <div className="space-y-4">
                  {briefData.crmIntegration.selectedCrms?.length > 0 && (
                    <div className="border-l-2 border-primary pl-3">
                      <p className="text-sm font-medium">CRM Platforms</p>
                      <div className="flex flex-wrap gap-2 mt-2">
                        {formatCrmNames(briefData.crmIntegration.selectedCrms, briefData.crmIntegration.customCrmNames).map((crmName, index) => (
                          <Badge key={`crm-${index}`} variant="secondary" className="text-xs">
                            {crmName}
                          </Badge>
                        ))}
                      </div>
                    </div>
                  )}

                  {briefData.crmIntegration.selectedMarketingAutomation?.length > 0 && (
                    <div className="border-l-2 border-primary pl-3">
                      <p className="text-sm font-medium">Marketing Automation Platforms</p>
                      <div className="flex flex-wrap gap-2 mt-2">
                        {formatMarketingAutomationNames(briefData.crmIntegration.selectedMarketingAutomation, briefData.crmIntegration.customMarketingAutomationNames).map((platformName, index) => (
                          <Badge key={`marketing-${index}`} variant="outline" className="text-xs">
                            {platformName}
                          </Badge>
                        ))}
                      </div>
                    </div>
                  )}

                  {briefData.crmIntegration.selectedPaymentGateways?.length > 0 && (
                    <div className="border-l-2 border-primary pl-3">
                      <p className="text-sm font-medium">Payment Gateways</p>
                      <div className="flex flex-wrap gap-2 mt-2">
                        {formatPaymentGatewayNames(briefData.crmIntegration.selectedPaymentGateways, briefData.crmIntegration.customPaymentGatewayNames).map((gatewayName, index) => (
                          <Badge key={`payment-${index}`} variant="default" className="text-xs">
                            {gatewayName}
                          </Badge>
                        ))}
                      </div>
                    </div>
                  )}

                  {briefData.crmIntegration.apiIntegrations?.trim() && (
                    <div className="border-l-2 border-primary pl-3">
                      <p className="text-sm font-medium">API Integrations</p>
                      <div className="mt-2 p-3 bg-muted rounded-md">
                        <p className="text-sm text-muted-foreground whitespace-pre-wrap">
                          {briefData.crmIntegration.apiIntegrations}
                        </p>
                      </div>
                    </div>
                  )}

                  {briefData.crmIntegration.selectedAutomationPlatforms?.length > 0 && (
                    <div className="border-l-2 border-primary pl-3">
                      <p className="text-sm font-medium">Automation Platforms</p>
                      <div className="flex flex-wrap gap-2 mt-2">
                        {formatAutomationPlatformNames(briefData.crmIntegration.selectedAutomationPlatforms).map((platformName, index) => (
                          <Badge key={`automation-${index}`} variant="destructive" className="text-xs">
                            {platformName}
                          </Badge>
                        ))}
                      </div>
                    </div>
                  )}

                  {briefData.crmIntegration.selectedEngagementFeatures?.length > 0 && (
                    <div className="border-l-2 border-primary pl-3">
                      <p className="text-sm font-medium">Engagement & Interactivity</p>
                      <div className="flex flex-wrap gap-2 mt-2">
                        {formatEngagementFeatureNames(briefData.crmIntegration.selectedEngagementFeatures).map((featureName, index) => (
                          <Badge key={`engagement-${index}`} className="text-xs bg-gradient-to-r from-purple-500 to-pink-500 text-white">
                            {featureName}
                          </Badge>
                        ))}
                      </div>
                    </div>
                  )}

                  {briefData.crmIntegration.selectedAdvancedFeatures && briefData.crmIntegration.selectedAdvancedFeatures.length > 0 && (
                    <div className="border-l-2 border-primary pl-3">
                      <p className="text-sm font-medium">Advanced Features</p>
                      <div className="flex flex-wrap gap-2 mt-2">
                        {formatAdvancedFeatureNames(briefData.crmIntegration.selectedAdvancedFeatures).map((featureName, index) => (
                          <Badge key={`advanced-${index}`} className="text-xs bg-gradient-to-r from-orange-500 to-red-500 text-white">
                            {featureName}
                          </Badge>
                        ))}
                      </div>
                    </div>
                  )}

                  {briefData.crmIntegration.selectedECommerceFeatures && briefData.crmIntegration.selectedECommerceFeatures.length > 0 && (
                    <div className="border-l-2 border-primary pl-3">
                      <p className="text-sm font-medium">E-Commerce</p>
                      <div className="flex flex-wrap gap-2 mt-2">
                        {formatECommerceFeatureNames(briefData.crmIntegration.selectedECommerceFeatures).map((featureName, index) => (
                          <Badge key={`ecommerce-${index}`} className="text-xs bg-gradient-to-r from-green-500 to-blue-500 text-white">
                            {featureName}
                          </Badge>
                        ))}
                      </div>
                    </div>
                  )}
                </div>
              </div>
            )}

            {/* User Accounts & Membership */}
            {briefData.userAccountsMembership && (briefData.userAccountsMembership.registrationLogin || briefData.userAccountsMembership.userDashboardNeeded || (briefData.userAccountsMembership.predefinedRoles?.length > 0 || briefData.userAccountsMembership.customRoles?.length > 0) || briefData.userAccountsMembership.membershipSubscriptionSystem) && (
              <div>
                <div className="flex items-center justify-between mb-4">
                  <h4 className="font-semibold">User Accounts & Membership</h4>
                  <Button 
                    variant="ghost" 
                    size="sm" 
                    onClick={() => onEditSection('membership')}
                    data-testid="button-edit-membership"
                  >
                    Edit
                  </Button>
                </div>
                <div className="space-y-4">

                  {/* Registration & Login */}
                  {briefData.userAccountsMembership.registrationLogin && (
                    <div className="border-l-2 border-primary pl-3">
                      <p className="text-sm font-medium">User Registration & Login</p>
                      <Badge 
                        className="text-xs mt-2 bg-gradient-to-r from-green-500 to-emerald-500 text-white"
                        data-testid="status-registration-login"
                      >
                        Registration & Login Enabled
                      </Badge>
                      <p className="text-xs text-muted-foreground mt-2">
                        Users will be able to create accounts and log in to access personalized features
                      </p>
                    </div>
                  )}

                  {/* User Dashboard */}
                  {briefData.userAccountsMembership.userDashboardNeeded && (
                    <div className="border-l-2 border-primary pl-3">
                      <p className="text-sm font-medium">User Dashboard</p>
                      <Badge 
                        className="text-xs mt-2 bg-gradient-to-r from-purple-500 to-violet-500 text-white"
                        data-testid="status-user-dashboard"
                      >
                        Dashboard Required
                      </Badge>
                      {briefData.userAccountsMembership.userDashboardFeatures && (
                        <div className="mt-3">
                          <p className="text-xs text-muted-foreground mb-2">Dashboard Features:</p>
                          <div className="p-3 bg-muted rounded-md">
                            <p 
                              className="text-sm text-muted-foreground whitespace-pre-wrap"
                              data-testid="text-dashboard-features"
                            >
                              {briefData.userAccountsMembership.userDashboardFeatures}
                            </p>
                          </div>
                        </div>
                      )}
                    </div>
                  )}

                  {(briefData.userAccountsMembership.predefinedRoles?.length > 0 || briefData.userAccountsMembership.customRoles?.length > 0) && (
                    <div className="border-l-2 border-primary pl-3">
                      <p className="text-sm font-medium">User Roles</p>

                      {briefData.userAccountsMembership.predefinedRoles?.length > 0 && (
                        <div className="mt-3">
                          <p className="text-xs text-muted-foreground mb-2">Predefined Roles:</p>
                          <div className="flex flex-wrap gap-2">
                            {briefData.userAccountsMembership.predefinedRoles.map((role, index) => (
                              <Badge key={`predefined-role-${index}`} variant="outline" className="text-xs capitalize">
                                {role}
                              </Badge>
                            ))}
                          </div>
                        </div>
                      )}

                      {briefData.userAccountsMembership.customRoles?.length > 0 && (
                        <div className="mt-3">
                          <p className="text-xs text-muted-foreground mb-2">Custom Roles:</p>
                          <div className="space-y-3">
                            {briefData.userAccountsMembership.customRoles.map((role, index) => (
                              <div key={`custom-role-${index}`} className="p-3 bg-muted rounded-md">
                                <div className="flex items-center gap-2 mb-2">
                                  <Badge variant="secondary" className="text-xs">
                                    {role.name}
                                  </Badge>
                                </div>
                                {role.description && (
                                  <p className="text-sm text-muted-foreground">
                                    {role.description}
                                  </p>
                                )}
                              </div>
                            ))}
                          </div>
                        </div>
                      )}
                    </div>
                  )}

                  {briefData.userAccountsMembership.membershipSubscriptionSystem && (
                    <div className="border-l-2 border-primary pl-3">
                      <p className="text-sm font-medium">Membership / Subscription System</p>
                      <Badge className="text-xs mt-2 bg-gradient-to-r from-blue-500 to-cyan-500 text-white">
                        Subscription System Enabled
                      </Badge>

                      {briefData.userAccountsMembership.membershipDetails && (
                        <div className="mt-3">
                          <p className="text-xs text-muted-foreground mb-2">Membership Details:</p>
                          <div className="p-3 bg-muted rounded-md">
                            <p className="text-sm text-muted-foreground whitespace-pre-wrap">
                              {briefData.userAccountsMembership.membershipDetails}
                            </p>
                          </div>
                        </div>
                      )}
                    </div>
                  )}

                </div>
              </div>
            )}

            {/* Images */}
            <div>
              <div className="flex items-center justify-between mb-4">
                <h4 className="font-semibold">Images & Media</h4>
                <Button 
                  variant="ghost" 
                  size="sm" 
                  onClick={() => onEditSection('media')}
                  data-testid="button-edit-media"
                >
                  Edit
                </Button>
              </div>
              <div className="space-y-2">
                {briefData.images.slice(0, 5).map((image, index) => (
                  <div key={index} className="flex items-center gap-3">
                    <Images className="w-4 h-4 text-muted-foreground" />
                    <div>
                      <p className="text-sm font-medium">{image.name}</p>
                      <p className="text-xs text-muted-foreground">
                        {(image.size / 1024 / 1024).toFixed(2)} MB
                      </p>
                    </div>
                  </div>
                ))}
                {briefData.images.length > 5 && (
                  <p className="text-sm text-muted-foreground">
                    +{briefData.images.length - 5} more images
                  </p>
                )}
                {briefData.images.length === 0 && (
                  <p className="text-sm text-muted-foreground">No images uploaded yet</p>
                )}
              </div>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/Dashboard.tsx.content:
import { Card, CardHeader, CardTitle, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { 
  Plus, 
  Eye, 
  Edit, 
  Download,
  Clock,
  CheckCircle,
  AlertCircle,
  FileText
} from "lucide-react";

export interface Project {
  id: string;
  name: string;
  status: 'draft' | 'in_review' | 'approved' | 'in_progress' | 'completed';
  createdAt: string;
  updatedAt: string;
  type: string;
  progress: number;
}

interface DashboardProps {
  projects: Project[];
  userRole: 'client' | 'admin';
  onCreateProject: () => void;
  onViewProject: (id: string) => void;
  onEditProject: (id: string) => void;
  onExportProject: (id: string) => void;
  className?: string;
}

const statusConfig = {
  draft: { label: 'Draft', color: 'bg-gray-500', icon: Clock },
  in_review: { label: 'In Review', color: 'bg-yellow-500', icon: AlertCircle },
  approved: { label: 'Approved', color: 'bg-green-500', icon: CheckCircle },
  in_progress: { label: 'In Progress', color: 'bg-blue-500', icon: Clock },
  completed: { label: 'Completed', color: 'bg-green-600', icon: CheckCircle }
};

export default function Dashboard({ 
  projects, 
  userRole, 
  onCreateProject,
  onViewProject,
  onEditProject,
  onExportProject,
  className = "" 
}: DashboardProps) {
  const getStatusInfo = (status: Project['status']) => {
    return statusConfig[status] || statusConfig.draft;
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      year: 'numeric'
    });
  };

  return (
    <div className={`space-y-6 ${className}`}>
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold">
            {userRole === 'admin' ? 'Admin Dashboard' : 'My Projects'}
          </h1>
          <p className="text-muted-foreground mt-2">
            {userRole === 'admin' 
              ? 'Manage all client projects and creative briefs'
              : 'Track your website projects and creative briefs'
            }
          </p>
        </div>
        {userRole === 'client' && (
          <Button onClick={onCreateProject} className="gap-2" data-testid="button-create-project">
            <Plus className="w-4 h-4" />
            Start New Project
          </Button>
        )}
      </div>

      {/* Stats Overview */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card>
          <CardContent className="pt-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-muted-foreground">Total Projects</p>
                <p className="text-2xl font-bold">{projects.length}</p>
              </div>
              <FileText className="w-8 h-8 text-muted-foreground" />
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="pt-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-muted-foreground">In Progress</p>
                <p className="text-2xl font-bold">
                  {projects.filter(p => p.status === 'in_progress').length}
                </p>
              </div>
              <Clock className="w-8 h-8 text-blue-500" />
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="pt-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-muted-foreground">Completed</p>
                <p className="text-2xl font-bold">
                  {projects.filter(p => p.status === 'completed').length}
                </p>
              </div>
              <CheckCircle className="w-8 h-8 text-green-500" />
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="pt-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-muted-foreground">Pending Review</p>
                <p className="text-2xl font-bold">
                  {projects.filter(p => p.status === 'in_review').length}
                </p>
              </div>
              <AlertCircle className="w-8 h-8 text-yellow-500" />
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Projects List */}
      <Card>
        <CardHeader>
          <CardTitle>Recent Projects</CardTitle>
        </CardHeader>
        <CardContent>
          {projects.length === 0 ? (
            <div className="text-center py-8">
              <FileText className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
              <p className="text-lg font-medium mb-2">No projects yet</p>
              <p className="text-muted-foreground mb-4">
                {userRole === 'client' 
                  ? "Start your first website project by creating a creative brief"
                  : "No client projects have been submitted yet"
                }
              </p>
              {userRole === 'client' && (
                <Button onClick={onCreateProject} data-testid="button-create-first-project">
                  Create Your First Project
                </Button>
              )}
            </div>
          ) : (
            <div className="space-y-4">
              {projects.map((project) => {
                const statusInfo = getStatusInfo(project.status);
                const StatusIcon = statusInfo.icon;

                return (
                  <div key={project.id} className="flex items-center justify-between p-4 border rounded-lg hover-elevate">
                    <div className="flex items-center space-x-4">
                      <div className="flex-shrink-0">
                        <StatusIcon className="w-5 h-5 text-muted-foreground" />
                      </div>

                      <div className="flex-1 min-w-0">
                        <div className="flex items-center space-x-3 mb-2">
                          <h3 className="font-semibold truncate">{project.name}</h3>
                          <Badge variant="outline" className="flex items-center gap-1">
                            <div className={`w-2 h-2 rounded-full ${statusInfo.color}`} />
                            {statusInfo.label}
                          </Badge>
                        </div>

                        <div className="flex items-center space-x-4 text-sm text-muted-foreground">
                          <span>Type: {project.type}</span>
                          <span>Created: {formatDate(project.createdAt)}</span>
                          <span>Updated: {formatDate(project.updatedAt)}</span>
                          {project.progress > 0 && (
                            <span>Progress: {project.progress}%</span>
                          )}
                        </div>
                      </div>
                    </div>

                    <div className="flex items-center space-x-2">
                      <Button 
                        variant="ghost" 
                        size="sm"
                        onClick={() => onViewProject(project.id)}
                        data-testid={`button-view-project-${project.id}`}
                      >
                        <Eye className="w-4 h-4" />
                      </Button>

                      {(userRole === 'client' && project.status === 'draft') || userRole === 'admin' ? (
                        <Button 
                          variant="ghost" 
                          size="sm"
                          onClick={() => onEditProject(project.id)}
                          data-testid={`button-edit-project-${project.id}`}
                        >
                          <Edit className="w-4 h-4" />
                        </Button>
                      ) : null}

                      <Button 
                        variant="ghost" 
                        size="sm"
                        onClick={() => onExportProject(project.id)}
                        data-testid={`button-export-project-${project.id}`}
                      >
                        <Download className="w-4 h-4" />
                      </Button>
                    </div>
                  </div>
                );
              })}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

