            <div 
              className="w-16 h-16 rounded-md border-2 border-border flex items-center justify-center text-xs font-medium"
              style={{ 
                backgroundColor: preferences.accentColor || "#60A5FA",
                color: preferences.textColor || "#1F2937"
              }}
