client/src/components/SitemapBuilder.tsx.content:
import { useState } from "react";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { 
  Plus, 
  X, 
  GripVertical, 
  Home, 
  FileText, 
  Users, 
  Mail,
  ShoppingBag,
  Briefcase
} from "lucide-react";

export interface Page {
  id: string;
  name: string;
  path: string;
  required: boolean;
  icon?: React.ComponentType<{ className?: string }>;
}

interface SitemapBuilderProps {
  pages: Page[];
  onPagesUpdate: (pages: Page[]) => void;
  suggestedPages?: Page[];
  className?: string;
}

const pageIcons: Record<string, React.ComponentType<{ className?: string }>> = {
  home: Home,
  about: Users,
  services: Briefcase,
  products: ShoppingBag,
  contact: Mail,
  blog: FileText
};

export default function SitemapBuilder({ 
  pages, 
  onPagesUpdate, 
  suggestedPages = [],
  className = "" 
}: SitemapBuilderProps) {
  const [newPageName, setNewPageName] = useState("");
  const [showSuggestions, setShowSuggestions] = useState(true);

  const addPage = (page?: Page) => {
    if (page) {
      // Add suggested page
      const newPages = [...pages, page];
      onPagesUpdate(newPages);
      console.log('Added suggested page:', page.name);
    } else if (newPageName.trim()) {
      // Add custom page
      const newPage: Page = {
        id: Date.now().toString(),
        name: newPageName.trim(),
        path: `/${newPageName.trim().toLowerCase().replace(/\s+/g, '-')}`,
        required: false
      };
      const newPages = [...pages, newPage];
      onPagesUpdate(newPages);
      setNewPageName("");
      console.log('Added custom page:', newPage.name);
    }
  };

  const removePage = (pageId: string) => {
    const pageToRemove = pages.find(p => p.id === pageId);
    if (pageToRemove && !pageToRemove.required) {
      const newPages = pages.filter(p => p.id !== pageId);
      onPagesUpdate(newPages);
      console.log('Removed page:', pageToRemove.name);
    }
  };

  const availableSuggestions = suggestedPages.filter(
    suggestion => !pages.some(page => page.id === suggestion.id)
  );

  return (
    <div className={`space-y-6 ${className}`}>
      {/* Current Pages */}
      <div>
        <h3 className="font-semibold text-lg mb-4">Your Website Pages</h3>
        <div className="space-y-3">
          {pages.map((page, index) => {
            const IconComponent = page.icon || pageIcons[page.name.toLowerCase()] || FileText;

            return (
              <Card key={page.id} className="p-4">
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-3">
                    <GripVertical className="w-4 h-4 text-muted-foreground cursor-grab" />
                    <IconComponent className="w-5 h-5 text-primary" />
                    <div>
                      <div className="font-medium">{page.name}</div>
                      <div className="text-sm text-muted-foreground">{page.path}</div>
                    </div>
                    {page.required && (
                      <Badge variant="outline" className="ml-2">Required</Badge>
                    )}
                  </div>

                  {!page.required && (
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => removePage(page.id)}
                      data-testid={`button-remove-page-${page.id}`}
                    >
                      <X className="w-4 h-4" />
                    </Button>
                  )}
                </div>
              </Card>
            );
          })}
        </div>
      </div>

      {/* Add Custom Page */}
      <div>
        <h4 className="font-medium mb-3">Add Custom Page</h4>
        <div className="flex space-x-2">
          <Input
            placeholder="Page name (e.g., Portfolio, FAQ)"
            value={newPageName}
            onChange={(e) => setNewPageName(e.target.value)}
            onKeyDown={(e) => e.key === 'Enter' && addPage()}
            data-testid="input-new-page"
          />
          <Button
            onClick={() => addPage()}
            disabled={!newPageName.trim()}
            data-testid="button-add-page"
          >
            <Plus className="w-4 h-4 mr-2" />
            Add
          </Button>
        </div>
      </div>

      {/* Suggested Pages */}
      {availableSuggestions.length > 0 && showSuggestions && (
        <div>
          <div className="flex items-center justify-between mb-3">
            <h4 className="font-medium">Suggested Pages</h4>
            <Button
              variant="ghost"
              size="sm"
              onClick={() => setShowSuggestions(false)}
              data-testid="button-hide-suggestions"
            >
              Hide
            </Button>
          </div>
          <div className="grid grid-cols-2 md:grid-cols-3 gap-2">
            {availableSuggestions.map((suggestion) => {
              const IconComponent = suggestion.icon || pageIcons[suggestion.name.toLowerCase()] || FileText;

              return (
                <Button
                  key={suggestion.id}
                  variant="outline"
                  className="justify-start hover-elevate"
                  onClick={() => addPage(suggestion)}
                  data-testid={`button-add-suggested-${suggestion.id}`}
                >
                  <IconComponent className="w-4 h-4 mr-2" />
                  {suggestion.name}
                </Button>
              );
            })}
          </div>
        </div>
      )}
    </div>
  );
}

client/src/components/ThemeToggle.tsx.content:
import { Moon, Sun } from "lucide-react";
import { Button } from "@/components/ui/button";
import { useState, useEffect } from "react";

export default function ThemeToggle() {
  const [theme, setTheme] = useState<'light' | 'dark'>('light');

  useEffect(() => {
    const savedTheme = localStorage.getItem('theme') as 'light' | 'dark' | null;
    const systemTheme = window.matchMedia('(prefers-color-scheme: dark)').matches ? 'dark' : 'light';
    const initialTheme = savedTheme || systemTheme;

    setTheme(initialTheme);
    document.documentElement.classList.toggle('dark', initialTheme === 'dark');
  }, []);

  const toggleTheme = () => {
    const newTheme = theme === 'light' ? 'dark' : 'light';
    setTheme(newTheme);
    localStorage.setItem('theme', newTheme);
    document.documentElement.classList.toggle('dark', newTheme === 'dark');
    console.log('Theme toggled to:', newTheme);
  };

  return (
    <Button
      variant="ghost"
      size="icon"
      onClick={toggleTheme}
      data-testid="button-theme-toggle"
    >
      {theme === 'light' ? (
        <Moon className="h-4 w-4" />
      ) : (
        <Sun className="h-4 w-4" />
      )}
    </Button>
  );
}

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

client/src/hooks/use-toast.ts.content:
import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

client/src/lib/queryClient.ts.content:
import { QueryClient, QueryFunction } from "@tanstack/react-query";

async function throwIfResNotOk(res: Response) {
  if (!res.ok) {
    const text = (await res.text()) || res.statusText;
    throw new Error(`${res.status}: ${text}`);
  }
}

export async function apiRequest(
  method: string,
  url: string,
  data?: unknown | undefined,
): Promise<Response> {
  const res = await fetch(url, {
    method,
    headers: data ? { "Content-Type": "application/json" } : {},
    body: data ? JSON.stringify(data) : undefined,
    credentials: "include",
  });

  await throwIfResNotOk(res);
  return res;
}

type UnauthorizedBehavior = "returnNull" | "throw";
export const getQueryFn: <T>(options: {
  on401: UnauthorizedBehavior;
}) => QueryFunction<T> =
  ({ on401: unauthorizedBehavior }) =>
  async ({ queryKey }) => {
    const res = await fetch(queryKey.join("/") as string, {
      credentials: "include",
    });

    if (unauthorizedBehavior === "returnNull" && res.status === 401) {
      return null;
    }

    await throwIfResNotOk(res);
    return await res.json();
  };

export const queryClient = new QueryClient({
  defaultOptions: {
    queries: {
      queryFn: getQueryFn({ on401: "throw" }),
      refetchInterval: false,
      refetchOnWindowFocus: false,
      staleTime: Infinity,
      retry: false,
    },
    mutations: {
      retry: false,
    },
  },
});

